pragma solidity >=0.4.21 <0.6.0;
// import "openzeppelin-solidity/contracts/access/Roles.sol";

contract TumbulaStore{

    /*
    Contract for the Tumbula online store.

    ------------------------------------
    ROLES.
    1. Admin.
    2. Store Owner.
    ------------------------------------
     */


    address owner;

    uint public storeOwnerNumber = 0;

    struct StoreOwner{
        uint    id;
        string  first_name;
        string  last_name;
        bool    active;
    }

    mapping(uint => StoreOwner) public storeowners;

    event storeOwnerCreated(
        uint id,
        string first_name,
        string last_name,
        bool active
    );

    constructor() public {
        addStoreOwner("Katongole", "Allan");
        addStoreOwner("Assimirwe", "Abraham");
    }

    function addStoreOwner(string memory _first_name, string memory _last_name) public {
        storeowners[storeOwnerNumber] = StoreOwner(storeOwnerNumber, _first_name, _last_name, true);
        storeOwnerNumber = storeOwnerNumber + 1;
        emit storeOwnerCreated(storeOwnerNumber, _first_name, _last_name, true);
    }
}
